import React from 'react';
import './App.css';

class Data extends React.Component {
    constructor(props){
        super(props)
    }
    render(){
        // length of shower
        // average shower time, how much longer/shorter this time
        // start, peak, delta humidity
        // start, peak, delta temp
        const {length, averageLength, diffFromAverage, peakT, avgPeakT, diffT, avgDiffT, peakH, avgPeakH, diffH, avgDiffH} = this.props.data;
        return (
            <div className="Data">
                <div className="data-row">
                    <DataPoint data={length} label="length of shower" type="large"/>
                    <DataPoint data={averageLength} label="avg shower time" type="large"/>
                    <DataPoint length={diffFromAverage} label = {`${length > averageLength ? 'Longer':'Shorter'} than average`} type="large"/>
                </div>
                <div className="data-row">
                    <DataPoint data={peakT.toFixed(1)} label="Peak Temp" type="large"/>
                    <DataPoint data={avgPeakT.toFixed(1)} label="Average Peak" type="small"/>
                    <DataPoint data={diffT.toFixed(1)} label="Temp Increase" type="large"/>
                    <DataPoint data={avgDiffT.toFixed(1)} label="Average Increase" type="small"/>
                </div>
                <div className="data-row">
                    <DataPoint data={peakH.toFixed(1)} label="Peak Humidity" type="large"/>
                    <DataPoint data={avgPeakH.toFixed(1)} label="Average Peak" type="small"/>
                    <DataPoint data={diffH.toFixed(1)} label="Humidity Increase" type="large"/>
                    <DataPoint data={avgDiffH.toFixed(1)} label="Average Increase" type="small"/>
                </div>

            </div>
        );
    }
}

export default Data;

class DataPoint extends React.Component {
    constructor(props){
        super(props)
    }
    render(){
        const {data, label, type} = this.props;
        return(
            <div className="data-point">
                <div className={`data-point-${type}`}>{data}</div>
                <div className="data-point-label">{label}</div>
            </div>
        )
    }
}
